import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from "@angular/common/http";
import {Observable} from "rxjs";
import {KeycloakSecurityService} from "./keycloak-security.service";

@Injectable({
  providedIn: 'root'
})
export class AbsenceService {

  constructor(private http:HttpClient, private key:KeycloakSecurityService) { }

  public getAbsences(): Observable<any>{
    let headers = new HttpHeaders();
    headers = headers.set('Content-Type', 'application/json; charset=utf-8')
          .set('Authorization', 'Bearer ' + this.key.kc.token);

    let body = new URLSearchParams();
    body.set('grant_type', 'password');
    body.set('client_id', 'jee-client');
    body.set('client_secret', 'AND56mEdZgaQQ3VWUNjb7kVS0VueRAXh');
    body.set('username', 'test');
    body.set('password', 'test');

    return this.http.get("http://localhost:8080/STUDENT-SERVICE/students",
        body, {headers: headers});
  };

}
